import React, {Component, useState} from 'react';
import './App.css';
import * as ReactBootStrap from "react-bootstrap"; 
import { Layout, Container, BoxUpload, ImagePreview} from './style';
import x from './x.svg'; 


function App() {
  const [image, setImage] = useState('')
  const [isUploaded, setIsUploaded] = useState(false)
  function handleImageChange(e) { 
      if (e.target.files && e.target.files[0]) {
        let reader = new FileReader() 
  
        reader.onload = function(e) { 
          setImage(e.target.result) 
          setIsUploaded(true)
        }
        reader.readAsDataURL(e.target.files[0])
      }
  }
  
    return (
    <div className="App">
      <ReactBootStrap.Navbar collapseOnSelect expand="lg" bg="dark" variant="dark">
  <ReactBootStrap.Navbar.Brand href="#home">DESCRYPT-LOGO</ReactBootStrap.Navbar.Brand>
  <ReactBootStrap.Navbar.Toggle aria-controls="responsive-navbar-nav" />
  <ReactBootStrap.Navbar.Collapse id="responsive-navbar-nav">
    <ReactBootStrap.Nav className="container-fluid">
    <ReactBootStrap.NavDropdown title="Profile" id="collasible-nav-dropdown">
        <ReactBootStrap.NavDropdown.Item href="#myCheatsheets">My Cheatsheets</ReactBootStrap.NavDropdown.Item>
        <ReactBootStrap.NavDropdown.Item href="#sharedWithMe">Shared with me</ReactBootStrap.NavDropdown.Item>
        <ReactBootStrap.NavDropdown.Divider />
        <ReactBootStrap.NavDropdown.Item href="#signOut">Sign Out</ReactBootStrap.NavDropdown.Item>
      </ReactBootStrap.NavDropdown>
    </ReactBootStrap.Nav>
  </ReactBootStrap.Navbar.Collapse>
</ReactBootStrap.Navbar>
   
   <Layout> 
    <Container> 
      <h3> Upload files here</h3>
      <p> Preview</p>
  
    <BoxUpload>
    <div className="file-upload">
    { 
        !isUploaded ? ( 
        <> 
          <label htmlFor="upload-input"> 
      <img src='data:image/png;base64,' draggable ={'false'} alt ="resize-img-upload-icon" style={{width: 50, height:50}}></img>  
      <p style ={{ color: '#444'}}> Click to upload file </p>
      </label>
      <input 
      id="upload-input" 
      type="file" 
      accept=".jpg, .pdf, .jpeg, .png"
        onChange={handleImageChange}
      /> 
      </>
        ) : ( 
          <ImagePreview>
          <img className="x" 
          src={x} 
          alt="CloseIcon"
          onClick={() => {
          setIsUploaded(false)
          setImage(null)}} />
           <img id ="uploaded-image"
            src={image} 
            alt="uploaded-image"
            draggable={false}
            />
          </ImagePreview>
        )

    }
      
      </div>
    </BoxUpload> 
  
    </Container> 
   </Layout> 
   
   
    </div>
    
  

  );
}


export default App;
